import React , { useState , FC } from 'react';
import PromptPiece from './PromptPiece';

interface PromptBuilderProps {
  appState: any
  onPromptPartWeightInputChange: any
}

const generateOutput = (appState: any) => {
  return 'this will be the output';
};

const PromptBuilder: FC<PromptBuilderProps> = ({
  appState,
  onPromptPartWeightInputChange
}) => {

  const [promptParts, setPromptParts] = useState(appState.promptParts);
  //const promptParts = appState.promptParts;

  const handlePieceChange = (index: number, e: React.ChangeEvent<HTMLInputElement>) => {
    const partState = promptParts[index];
  };

  console.log('rerendering: PromptBuilder component');
  return (
    <div className="promptBuilder">
      <div>
        {promptParts.map((partData: any, index: number) => {
          const promptPieceProps = {
            onChange: handlePieceChange,
            key: index,
            index: index,
            weight: partData.weight,
            rawPromptPart: partData.text
          };
          return (
            <PromptPiece { ...promptPieceProps } />
          );
        })}
      </div>

      <div>
        <p>Output: {generateOutput(appState)}</p>
      </div>
    </div>
  )
};

export default PromptBuilder
