import React , { useState } from 'react';
import './App.css';
import PromptBuilder from './components/PromptBuilder';
import RawPromptInput from './components/RawPromptInput';

const App: React.FC = () => {
  const [inputState, setInputState] = useState('');
  const [promptParts, setPromptParts] = useState([]);

  const [appState, setAppState] = useState({
    rawPrompt: '',
    promptParts: [{text: '', weight: '1'}]
  });

  const onRawPromptInputChange = (e: React.ChangeEvent<HTMLInputElement>) => setAppState({ ...appState , rawPrompt: e.target.value });

  const rawPromptInputProps = {
    onChange: onRawPromptInputChange
  }

  const onPromptPartWeightInputChange = (promptIndex: number, e: React.ChangeEvent<HTMLInputElement>) => {
    let newAppState = appState;
    newAppState.promptParts[promptIndex].weight = e.target.value;
    setAppState(newAppState);
  };

  const promptBuilderProps = {
    appState: appState,
    onPromptPartWeightInputChange: onPromptPartWeightInputChange
  }

  console.log('rerendering: App component');
  return (
    <div className="App">
      <header>
        <p>Create your Horizon</p>
      </header>
      <div className="App-body">
        <RawPromptInput { ...rawPromptInputProps } />
        <PromptBuilder rawPrompt={inputState} />
      </div>
    </div>
  );
};

export default App;
