import React , { HTMLInputTypeAttribute , MouseEvent , FC } from 'react';

interface GlobalParamsProps {
  appState: any,
  // maybe a change handler
}

const configureSuffix = (e: MouseEvent<HTMLButtonElement>) => {
  console.log(e);
};

const suffixInputVisible: boolean = (boolean) => {
  return true;
};

const GlobalParams: FC<GlobalParamsProps> = ({
  appState
}) => {
  return (
    <div className="globalParams">
      <p>Parameter configuration</p>
      <div className="globalParamsSuffix">
        <button onClick={configureSuffix}>Click to configure suffix</button>
        {suffixInputVisible && <input type="text"></input>}
      </div>
      <div>
        <button>Click to add artist</button>
      </div>
    </div>
  )
};

export default GlobalParams;
