import React , { ChangeEvent, FC , useState } from 'react';
import PromptPiece from './PromptPiece';

interface PromptBuilderProps {
  rawPrompt: string
}

const generateOutput = (builderState: any) => {
  return 'this will be the output';
};

const splitPrompt = (rawPrompt: string) => {
  let splitParts = rawPrompt.split('::');
  return splitParts.filter((part) => {
    return part.trim() !== '';
  });
};

const PromptBuilder: FC<PromptBuilderProps> = ({
  rawPrompt
}) => {
  const rawParts = splitPrompt(rawPrompt);
  const partData = rawParts.map((rawPromptPart, index) => {
    return {
      text: rawPromptPart
    }
  });
  console.log('partData', partData);
  const [builderState, setBuilderState] = useState({
    rawPrompt: rawPrompt,
    parts: partData
  });
  const promptSplitIntoParts: string[] = splitPrompt(rawPrompt);

  const handlePieceChange = (index: number, e: React.ChangeEvent<HTMLInputElement>) => {
    console.log('handlePieceChange => index', index);
    const partState = builderState.parts[index];
    console.log('handlePieceChange => builderState.parts[index]', builderState.parts);
    console.log('handlePieceChange', builderState);
  };

  return (
    <div className="promptBuilder">
      <div>
        <div>
          {promptSplitIntoParts.map((rawPromptPartVal, index) => {
            return (
              <PromptPiece onChange={handlePieceChange} key={index} index={index} rawPromptPart={rawPromptPartVal} />
            );
          })}
        </div>
      </div>
      <div>
        <p>Output: {generateOutput(builderState)}</p>
      </div>
    </div>
  )
};

export default PromptBuilder
