import React , { ChangeEvent, FC , useState } from 'react';

// inner PromptWeight component
interface PromptWeightProps {
  index: number
  onChange: any
}

const PromptWeight: FC<PromptWeightProps> = ({
  index,
  onChange
}) => {
  const onPromptWeightInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const evtVal = e.target.value;
    onChange(index, evtVal);
  };
  return (
    <div className='App.PromptWeight'>
      <input value='1' id="promptWeight" type="text" onChange={onPromptWeightInputChange} />
    </div>
  )
};

// main PromptPiece component
interface PromptPieceProps {
  index: number
  rawPromptPart: string
  onChange: any // TODO type this function
}

const PromptPiece: FC<PromptPieceProps> = ({
  index,
  rawPromptPart,
  onChange
}) => {
  const [renderedPromptPiece, setRenderedPromptPiece] = useState(rawPromptPart);
  const promptWeightProps = {
    onChange: onChange,
    index: index
  };

  return (
    <div>
      <div className='App.PromptPiece'>
        <p>[{index}] {rawPromptPart}</p>
        <PromptWeight { ...promptWeightProps } />
      </div>
    </div>
  )
};

export default PromptPiece
